import React from "react";
import "./Nautstring.css"

class Nautstring extends React.Component{
	constructor(props){
		super(props);
		this.state={
			inputstring : "",
			output: []
		}
		this.updateInput = this.updateInput.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
	};

	updateInput(event){
		this.setState({inputstring : event.target.value})
	}

	handleSubmit(event){
		event.preventDefault();
	}

	reverse = (str) => {
		let reversed = "";
		for (var i = str.length - 1; i >= 0; i--){
			reversed += str[i];
			this.setState({
				output: this.state.output.push(str[i])
			})
		}
		return reversed;
	}

	render(){
		return(
		<div className="Nautstring">
			<header className="Nautstring-header">
				<h1>What is this????????</h1>
			</header>
			<form>
				<input type="text" name="cstring" id="cstring" onChange={this.updateInput} />
				<button onClick={this.handleSubmit} > Submit </button>
			</form>
			<div className="inputstring">{this.reverse(this.state.inputstring)}</div>

    </div>
 		);
	}
};

export default Nautstring;
